import React, { useState } from 'react';
import axios from 'axios';

export default function VigenerePage() {
  const [plaintext, setPlaintext] = useState('');
  const [key, setKey] = useState('');
  const [ciphertext, setCiphertext] = useState('');
  const [decryptedText, setDecryptedText] = useState('');
  const [file, setFile] = useState(null);
  const [fileResult, setFileResult] = useState('');
  const [error, setError] = useState('');

  const handleFileUpload = (e) => {
    setFile(e.target.files[0]);
  };

  const encryptOrDecryptFile = async (action) => {
    if (!file || !key) {
      setError('Please select a file and enter a key.');
      return;
    }

    const formData = new FormData();
    formData.append('file', file);
    formData.append('key', key);

    try {
      const response = await axios.post(`http://localhost:5000/${action}-file/vigenere`, formData, {
        responseType: 'blob',
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      const blob = new Blob([response.data]);
      const url = URL.createObjectURL(blob);
      setFileResult(url);
      setError('');
    } catch {
      setError('File operation failed.');
    }
  };

  const encryptText = async () => {
    const res = await axios.post('http://localhost:5000/encrypt/vigenere', { plaintext, key });
    setCiphertext(res.data.ciphertext);
  };

  const decryptText = async () => {
    const res = await axios.post('http://localhost:5000/decrypt/vigenere', { ciphertext, key });
    setDecryptedText(res.data.plaintext);
  };

  return (
    <div className="container text-white mt-4">
      <h2>Vigen√®re Cipher</h2>
      {error && <div className="alert alert-danger">{error}</div>}
      <input className="form-control my-2" placeholder="Plaintext" value={plaintext} onChange={(e) => setPlaintext(e.target.value)} />
      <input className="form-control my-2" placeholder="Key" value={key} onChange={(e) => setKey(e.target.value)} />
      <div className="d-flex gap-2 mb-3">
        <button className="btn btn-success" onClick={encryptText}>Encrypt</button>
        <button className="btn btn-warning" onClick={decryptText}>Decrypt</button>
      </div>
      <p><strong>Ciphertext:</strong> {ciphertext}</p>
      <p><strong>Decrypted:</strong> {decryptedText}</p>

      <hr />
      <h4>File Encryption</h4>
      <input type="file" className="form-control my-2" onChange={handleFileUpload} />
      <div className="d-flex gap-2 mb-2">
        <button className="btn btn-outline-success" onClick={() => encryptOrDecryptFile('encrypt')}>Encrypt File</button>
        <button className="btn btn-outline-warning" onClick={() => encryptOrDecryptFile('decrypt')}>Decrypt File</button>
      </div>
      {fileResult && <a href={fileResult} className="btn btn-primary" download>Download</a>}
    </div>
  );
}
