  const [activeTab, setActiveTab] = useState("text");
  const [plaintext, setPlaintext] = useState("");
  const [key, setKey] = useState("");
  const [ciphertext, setCiphertext] = useState("");
  const [decryptedText, setDecryptedText] = useState("");
  const [error, setError] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [file, setFile] = useState(null);
  const [fileResult, setFileResult] = useState("");
  const [downloadProgress, setDownloadProgress] = useState(0);
  const [isDownloading, setIsDownloading] = useState(false);
  const [visualization, setVisualization] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);

  const validateInputs = () => {
    if (!plaintext.trim() && !file) {
      setError("Please enter text or upload a file");
      return false;
    }
    if (!key.trim()) {
      setError("Please enter a keyword");
      return false;
    }
    const uniqueChars = new Set(key.toUpperCase().split(""));
    if (uniqueChars.size < 2) {
      setError("Keyword must have at least 2 unique characters");
      return false;
    }
    setError("");
    return true;
  };

  const encryptText = async () => {
    if (!validateInputs()) return;

    try {
      setIsProcessing(true);
      const response = await fetch(
        "http://localhost:5000/encrypt/transposition",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ plaintext, key }),
        }
      );

      const data = await response.json();
      if (response.ok) {
        setCiphertext(data.ciphertext);
        setError("");

        // Create visualization
        const numRows = Math.ceil(data.paddedLength / data.columnOrder.length);
        const matrix = [];

        // Fill the matrix row by row
        for (let i = 0; i < numRows; i++) {
          const start = i * data.columnOrder.length;
          const end = start + data.columnOrder.length;
          matrix[i] = data.ciphertext.substring(start, end).split("");
        }

        setVisualization({
          matrix,
          columnOrder: data.columnOrder,
          originalLength: data.originalLength,
          paddedLength: data.paddedLength,
          key: key.toUpperCase(),
        });
      } else {
        setError(data.error);
      }
    } catch (err) {
      setError(
        "Failed to encrypt text. Please check your input and try again."
      );
    } finally {
      setIsProcessing(false);
    }
  };

  const decryptText = async () => {
    if (!validateInputs()) return;

    try {
      setIsProcessing(true);
      const response = await fetch(
        "http://localhost:5000/decrypt/transposition",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ ciphertext, key }),
        }
      );

      const data = await response.json();
      if (response.ok) {
        setDecryptedText(data.plaintext);
        setError("");

        // Create visualization for decryption
        const numRows = Math.ceil(ciphertext.length / key.length);
        const matrix = [];

        // Fill the matrix row by row
        for (let i = 0; i < numRows; i++) {
          const start = i * key.length;
          const end = start + key.length;
          matrix[i] = ciphertext.substring(start, end).split("");
        }

        // Calculate column order for decryption
        const keyChars = key.toUpperCase().split("");
        const sortedChars = [...keyChars].sort();
        const charOrder = {};

        let currentIndex = 0;
        for (const char of sortedChars) {
          if (!(char in charOrder)) {
            charOrder[char] = currentIndex++;
          }
        }

        const columnOrder = keyChars
          .map((char) => charOrder[char])
          .map((order, i) => ({ char: keyChars[i], order }))
          .sort((a, b) => a.order - b.order)
          .map((item) => item.char);

        setVisualization({
          matrix,
          columnOrder,
          originalLength: ciphertext.length,
          paddedLength: ciphertext.length,
          key: key.toUpperCase(),
          isDecryption: true,
        });
      } else {
        setError(data.error);
      }
    } catch (err) {
      setError(
        "Failed to decrypt text. Please check your input and try again."
      );
    } finally {
      setIsProcessing(false);
    }
  };

  const generateKey = () => {
    // Generate a random keyword with at least 2 unique characters
    const characters = "ABCDEFGHIJKLMNOPQRSTUVWXYZ";
    const length = Math.floor(Math.random() * 4) + 3; // Random length between 3 and 6
    let keyword = "";

    // Ensure at least 2 unique characters
    const firstChar = characters.charAt(
      Math.floor(Math.random() * characters.length)
    );
    const secondChar = characters.charAt(
      Math.floor(Math.random() * characters.length)
    );
    keyword = firstChar + secondChar;

    // Add remaining characters
    for (let i = 2; i < length; i++) {
      keyword += characters.charAt(
        Math.floor(Math.random() * characters.length)
      );
    }

    setKey(keyword);
    setError("");
  };

  const handleFileUpload = (e) => setFile(e.target.files[0]);

  const encryptFile = async () => {
    if (!file || !key) return setError("Select a file and enter a key.");
    const formData = new FormData();
    formData.append("file", file);
    formData.append("key", key);
    try {
      setIsDownloading(true);
      setDownloadProgress(0);

      const res = await axios.post(
        "http://localhost:5000/encrypt-file/transposition",
        formData,
        {
          responseType: "blob",
          onDownloadProgress: (progressEvent) => {
            const percent = Math.round(
              (progressEvent.loaded * 100) / (progressEvent.total || 1)
            );
            setDownloadProgress(percent);
          },
        }
      );

      const blob = new Blob([res.data], { type: res.headers["content-type"] });
      const url = URL.createObjectURL(blob);
      setFileResult({ url, filename: "encrypted_" + file.name });
      setError("");
    } catch (err) {
      setError("File encryption failed.");
    } finally {
      setIsDownloading(false);
      setDownloadProgress(0);
    }
  };

  const decryptFile = async () => {
    if (!file || !key) return setError("Select a file and enter a key.");
    const formData = new FormData();
    formData.append("file", file);
    formData.append("key", key);
    try {
      setIsDownloading(true);
      setDownloadProgress(0);

      const res = await axios.post(
        "http://localhost:5000/decrypt-file/transposition",
        formData,
        {
          responseType: "blob",
          onDownloadProgress: (progressEvent) => {
            const percent = Math.round(
              (progressEvent.loaded * 100) / (progressEvent.total || 1)
            );
            setDownloadProgress(percent);
          },
        }
      );

      const blob = new Blob([res.data], { type: res.headers["content-type"] });
      const url = URL.createObjectURL(blob);
      setFileResult({ url, filename: "decrypted_" + file.name });
      setError("");
    } catch (err) {
      setError("File decryption failed.");
    } finally {
      setIsDownloading(false);
      setDownloadProgress(0);
    }
  };

  return (
    <div className="page-container">
      <h1>Transposition Cipher</h1>
      <p className="description">
        The Transposition Cipher rearranges the letters of the plaintext
        according to a keyword. The keyword determines the order of columns in
        the matrix. The text is written row by row and read column by column in
        the order specified by the keyword. All whitespace (spaces, tabs,
        newlines) is replaced with underscores (_), and any remaining empty
        spaces in the matrix are also filled with underscores. The cipher
        preserves the case of the original text.
      </p>

      <div className="d-flex justify-content-center mb-4">
        <div className="btn-group">
          <button
            className={`btn ${
              activeTab === "text" ? "btn-success" : "btn-outline-success"
            }`}
            onClick={() => setActiveTab("text")}
          >
            Text Encryption
          </button>
          <button
            className={`btn ${
              activeTab === "file" ? "btn-success" : "btn-outline-success"
            }`}
            onClick={() => setActiveTab("file")}
          >
            File Encryption
          </button>
        </div>
      </div>

      {error && (
        <div
          className="alert alert-danger alert-dismissible fade show"
          role="alert"
        >
          {error}
          <button
            type="button"
            className="btn-close"
            onClick={() => setError("")}
            aria-label="Close"
          ></button>
        </div>
      )}

      {activeTab === "text" && (
        <div className="row mb-4">
          <div className="col-md-6 border-end">
            <h4 className="text-success">Encrypt</h4>
            <div className="form-group mb-3">
              <label htmlFor="plaintext" className="form-label text-light">
                Plaintext
              </label>
              <textarea
                id="plaintext"
                className="form-control"
                placeholder="Enter text to encrypt"
                value={plaintext}
                onChange={(e) => setPlaintext(e.target.value)}
                rows="3"
              />
            </div>

            <div className="input-group mb-3">
              <input
                type="text"
                className="form-control"
                placeholder="Keyword"
                value={key}
                onChange={(e) => setKey(e.target.value)}
              />
              <button className="btn btn-success" onClick={generateKey}>
                🔐 Generate
              </button>
            </div>

            <button
              className="btn btn-success"
              onClick={encryptText}
              disabled={isProcessing}
            >
              {isProcessing ? (
                <>
                  <Loader2 className="me-2" size={20} />
                  Encrypting...
                </>
              ) : (
                "Encrypt"
              )}
            </button>
            <div className="mt-3">
              <label className="form-label text-light">Ciphertext:</label>
              <div className="p-2 bg-dark rounded">
                <code className="text-light">{ciphertext}</code>
              </div>
            </div>

            {visualization && (
              <div className="visualization-container">
                <h3>Transposition Cipher Visualization</h3>
                <div className="matrix-visualization">
                  <div className="matrix-header">
                    {visualization.columnOrder.map((char, index) => (
                      <div key={index} className="matrix-cell header">
                        {char}
                      </div>
                    ))}
                  </div>
                  {visualization.matrix.map((row, rowIndex) => (
                    <div key={rowIndex} className="matrix-row">
                      {row.map((cell, colIndex) => (
                        <div key={colIndex} className="matrix-cell">
                          {cell === "_" ? " _" : cell}
                        </div>
                      ))}
                    </div>
                  ))}
                </div>
                <div className="visualization-info">
                  <p>
                    Original length: {visualization.originalLength} characters
                  </p>
                  <p>Padded length: {visualization.paddedLength} characters</p>
                  <p>Reading direction: Column-wise in keyword order</p>
                  <p>
                    Note: All whitespace and empty spaces are replaced with
                    underscores (_)
                  </p>
                </div>
              </div>
            )}
          </div>

          <div className="col-md-6">
            <h4 className="text-warning">Decrypt</h4>
            <div className="form-group mb-3">
              <label htmlFor="ciphertext" className="form-label text-light">
                Ciphertext
              </label>
              <textarea
                id="ciphertext"
                className="form-control"
                placeholder="Enter text to decrypt"
                value={ciphertext}
                onChange={(e) => setCiphertext(e.target.value)}
                rows="3"
              />
            </div>

            <div className="input-group mb-3">
              <input
                type="text"
                className="form-control"
                placeholder="Key (text)"
                value={key}
                onChange={(e) => setKey(e.target.value)}
              />
              <button className="btn btn-success" onClick={generateKey}>
                🔐 Generate
              </button>
            </div>

            <button
              className="btn btn-warning"
              onClick={decryptText}
              disabled={isProcessing}
            >
              {isProcessing ? (
                <>
                  <Loader2 className="me-2" size={20} />
                  Decrypting...
                </>
              ) : (
                "Decrypt"
              )}
            </button>
            <div className="mt-3">
              <label className="form-label text-light">Decrypted Text:</label>
              <div className="p-2 bg-dark rounded">
                <code className="text-light">{decryptedText}</code>
              </div>
            </div>
          </div>
        </div>
      )}

      {activeTab === "file" && (
        <div>
          <div className="input-group mb-3">
            <input
              type="file"
              className="form-control"
              onChange={handleFileUpload}
            />
          </div>
          <div className="input-group mb-3">
            <input
              type="text"
              className="form-control"
              placeholder="Key (text)"
              value={key}
              onChange={(e) => setKey(e.target.value)}
            />
            <button className="btn btn-success" onClick={generateKey}>
              🔐 Generate
            </button>
          </div>
          <div className="d-flex gap-2">
            <button className="btn btn-outline-success" onClick={encryptFile}>
              <FileText className="me-2" size={20} />
              Encrypt File
            </button>
            <button className="btn btn-outline-warning" onClick={decryptFile}>
              <FileText className="me-2" size={20} />
              Decrypt File
            </button>
          </div>
          {isDownloading && (
            <div className="progress my-3 w-100">
              <div
                className="progress-bar progress-bar-striped progress-bar-animated bg-success"
                style={{ width: `${downloadProgress}%` }}
              >
                {downloadProgress}%
              </div>
            </div>
          )}
          {fileResult && (
            <a
              href={fileResult.url}
              className="btn btn-primary mt-3"
              download={fileResult.filename}
            >
              <Download className="me-2" size={20} />
              Download Result
            </a>
          )}
        </div>
      )}
    </div>
  );
}
