import { useState } from 'react';
import axios from 'axios';
import emojiRegex from 'emoji-regex';

const emojiMap = {
  A: "ðŸ˜€", B: "ðŸ˜‚", C: "ðŸ˜", D: "ðŸ˜Ž", E: "ðŸ˜­", F: "ðŸ˜¡", G: "ðŸ˜±", H: "ðŸ˜´",
  I: "ðŸ˜‡", J: "ðŸ˜ˆ", K: "ðŸ¤“", L: "ðŸ§", M: "ðŸ¤¯", N: "ðŸ¤©", O: "ðŸ¥³", P: "ðŸ˜µ",
  Q: "ðŸ˜·", R: "ðŸ¤ ", S: "ðŸ˜º", T: "ðŸ˜»", U: "ðŸ˜¼", V: "ðŸ™€", W: "ðŸ˜¿", X: "ðŸ˜¹",
  Y: "ðŸ™ˆ", Z: "ðŸ™‰", a: "ðŸ™Š", b: "ðŸ‘¶", c: "ðŸ‘§", d: "ðŸ‘¦", e: "ðŸ§’", f: "ðŸ‘©",
  g: "ðŸ‘¨", h: "ðŸ‘µ", i: "ðŸ‘´", j: "ðŸ‘²", k: "ðŸ§”", l: "ðŸ‘³", m: "ðŸ‘®", n: "ðŸ‘·",
  o: "ðŸ’‚", p: "ðŸ•µï¸", q: "ðŸ‘©â€âš•ï¸", r: "ðŸ‘¨â€âš•ï¸", s: "ðŸ‘©â€ðŸ«", t: "ðŸ‘¨â€ðŸ«", u: "ðŸ‘©â€ðŸ³",
  v: "ðŸ‘¨â€ðŸ³", w: "ðŸ‘©â€ðŸ”§", x: "ðŸ‘¨â€ðŸ”§", y: "ðŸ‘©â€ðŸ­", z: "ðŸ‘¨â€ðŸ­", 0: "ðŸ’¯", 1: "ðŸ”¢",
  2: "ðŸ” ", 3: "ðŸ”£", 4: "ðŸ”¤", 5: "ðŸ…°ï¸", 6: "ðŸ†Ž", 7: "ðŸ…±ï¸", 8: "ðŸ†‘", 9: "ðŸ†’",
  '+': "ðŸ†“", '/': "ðŸ†”", '=': "ðŸ†•"
};

const reverseEmojiMap = Object.fromEntries(
  Object.entries(emojiMap).map(([char, emoji]) => [emoji, char])
);

function encodeToEmoji(base64) {
  return base64.split('').map(char => emojiMap[char] || char).join('');
}

function decodeFromEmoji(emojiText) {
  const regex = emojiRegex();
  const decoded = [];
  let match;
  while ((match = regex.exec(emojiText)) !== null) {
    const emoji = match[0];
    decoded.push(reverseEmojiMap[emoji] || '?');
  }
  return decoded.join('');
}

export default function CustomPage2() {
  const [activeTab, setActiveTab] = useState('text');
  const [plaintext, setPlaintext] = useState('');
  const [ciphertext, setCiphertext] = useState('');
  const [decryptedText, setDecryptedText] = useState('');
  const [error, setError] = useState('');

  const [file, setFile] = useState(null);
  const [decryptedFile, setDecryptedFile] = useState(null);
  const [fileCipher, setFileCipher] = useState(null);

  const encryptText = () => {
    try {
      const base64 = btoa(plaintext);
      const emojiCipher = encodeToEmoji(base64);
      setCiphertext(emojiCipher);
      setError('');
    } catch {
      setError('Text encryption failed.');
    }
  };

  const decryptText = () => {
    try {
      const base64 = decodeFromEmoji(ciphertext);
      const decoded = atob(base64);
      setDecryptedText(decoded);
      setError('');
    } catch {
      setError('Text decryption failed.');
    }
  };

  const handleFileEncrypt = async () => {
    if (!file) return alert("No file selected.");
    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post('http://localhost:5000/encrypt-file/emojicode', formData, {
        responseType: 'blob'
      });

      const blob = new Blob([response.data], { type: 'application/octet-stream' });
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = `encrypted_${file.name}`;
      document.body.appendChild(link);
      link.click();
      setFileCipher(`encrypted_${file.name}`);
    } catch {
      alert('File encryption failed.');
    }
  };

  const handleFileDecrypt = async () => {
    if (!file) return alert("No file selected.");
    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post('http://localhost:5000/decrypt-file/emojicode', formData, {
        responseType: 'blob'
      });

      const blob = new Blob([response.data], { type: 'application/octet-stream' });
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = `decrypted_${file.name}`;
      document.body.appendChild(link);
      link.click();
      setDecryptedFile(`decrypted_${file.name}`);
    } catch {
      alert('File decryption failed.');
    }
  };

  return (
    <div className="container mt-4">
      <h1 className="text-light text-center">EC - Emojicode Cipher</h1>
      <p className="text-secondary text-center">
        This cipher converts Base64 into emojis. The emoji-to-character map is the key.
      </p>

      <div className="d-flex justify-content-center mb-4">
        <div className="btn-group">
          <button className={`btn ${activeTab === 'text' ? 'btn-success' : 'btn-outline-success'}`} onClick={() => setActiveTab('text')}>
            Text Encryption
          </button>
          <button className={`btn ${activeTab === 'file' ? 'btn-success' : 'btn-outline-success'}`} onClick={() => setActiveTab('file')}>
            File Encryption
          </button>
        </div>
      </div>

      {error && <div className="alert alert-danger">{error}</div>}

      {activeTab === 'text' && (
        <div className="row mb-4">
          <div className="col-md-6 border-end">
            <h4 className="text-success">Encrypt</h4>
            <textarea className="form-control mb-2" placeholder="Plaintext" value={plaintext} onChange={e => setPlaintext(e.target.value)} />
            <button className="btn btn-success" onClick={encryptText}>Encrypt</button>
            <p className="mt-3"><strong>Ciphertext:</strong> <code>{ciphertext}</code></p>
          </div>

          <div className="col-md-6">
            <h4 className="text-warning">Decrypt</h4>
            <textarea className="form-control mb-2" placeholder="Emoji Ciphertext" value={ciphertext} onChange={e => setCiphertext(e.target.value)} />
            <button className="btn btn-warning" onClick={decryptText}>Decrypt</button>
            <p className="mt-3"><strong>Decrypted:</strong> <code>{decryptedText}</code></p>
          </div>
        </div>
      )}

      {activeTab === 'file' && (
        <div className="row mb-4">
          <div className="col-md-6 border-end">
            <h4 className="text-success">Encrypt File</h4>
            <input type="file" className="form-control mb-2" onChange={(e) => setFile(e.target.files[0])} />
            <button className="btn btn-success" onClick={handleFileEncrypt}>Encrypt & Download</button>
            {fileCipher && <p className="mt-3"><strong>Encrypted File:</strong> {fileCipher}</p>}
          </div>

          <div className="col-md-6">
            <h4 className="text-warning">Decrypt File</h4>
            <input type="file" className="form-control mb-2" onChange={(e) => setFile(e.target.files[0])} />
            <button className="btn btn-warning" onClick={handleFileDecrypt}>Decrypt & Download</button>
            {decryptedFile && <p className="mt-3"><strong>Decrypted File:</strong> {decryptedFile}</p>}
          </div>
        </div>
      )}
    </div>
  );
}
