import { useState } from 'react';

export default function CustomPage() {
  const [plaintext, setPlaintext] = useState('');
  const [key, setKey] = useState('');
  const [ciphertext, setCiphertext] = useState('');
  const [decrypted, setDecrypted] = useState('');
  const [error, setError] = useState('');

  // ðŸ” Mutation Map for substitution (can be extended)
  const mutationMap = {
    a: '@', b: '8', c: '(', d: 'D', e: '3', f: 'F',
    g: '9', h: '#', i: '1', j: 'J', k: 'K', l: '1',
    m: 'M', n: 'N', o: '0', p: 'P', q: 'Q', r: 'R',
    s: '$', t: '7', u: 'U', v: 'V', w: 'W', x: '%',
    y: 'Y', z: '2'
  };

  const substitute = (text) => {
    return text.split('').map(char => {
      const lower = char.toLowerCase();
      return mutationMap[lower] || char;
    }).join('');
  };

  const reverseSubstitute = (text) => {
    const reverseMap = Object.fromEntries(Object.entries(mutationMap).map(([k, v]) => [v, k]));
    return text.split('').map(char => {
      return reverseMap[char] || char;
    }).join('');
  };

  const insertNoise = (text, key) => {
    const noise = '*#&!';
    let result = '';
    const interval = (key.length % 3) + 2;
    for (let i = 0; i < text.length; i++) {
      result += text[i];
      if ((i + 1) % interval === 0) {
        result += noise[i % noise.length];
      }
    }
    return result;
  };

  const removeNoise = (text, key) => {
    const interval = (key.length % 3) + 2;
    return text.split('').filter((_, i) => (i + 1) % (interval + 1) !== 0).join('');
  };

  const transpose = (text, key) => {
    const len = key.length;
    let chunks = [];
    for (let i = 0; i < text.length; i += len) {
      chunks.push(text.slice(i, i + len).split('').reverse().join(''));
    }
    return chunks.join('');
  };

  const untranspose = (text, key) => {
    const len = key.length;
    let chunks = [];
    for (let i = 0; i < text.length; i += len) {
      chunks.push(text.slice(i, i + len).split('').reverse().join(''));
    }
    return chunks.join('');
  };

  const encrypt = () => {
    if (!plaintext || !key) {
      setError('Please enter both plaintext and a key.');
      return;
    }
    setError('');
    let mutated = substitute(plaintext);
    let noisy = insertNoise(mutated, key);
    let transposed = transpose(noisy, key);
    setCiphertext(btoa(transposed)); // base64 for safe display
  };

  const decrypt = () => {
    if (!ciphertext || !key) {
      setError('Please enter both ciphertext and a key.');
      return;
    }
    setError('');
    try {
      let decoded = atob(ciphertext);
      let untransposed = untranspose(decoded, key);
      let denoised = removeNoise(untransposed, key);
      let original = reverseSubstitute(denoised);
      setDecrypted(original);
    } catch {
      setError('Decryption failed. Check the key and ciphertext.');
    }
  };

  return (
    <div className="container mt-4 text-white">
      <h1 className="text-center">Custom - Genetic Mutation Cipher</h1>
      <p className="text-secondary text-center">Applies character substitution, noise insertion, and transposition for layered encryption.</p>

      {error && <div className="alert alert-danger">{error}</div>}

      <div className="row mb-4">
        <div className="col-md-6 border-end">
          <h4 className="text-success">Encrypt</h4>
          <input className="form-control mb-2" placeholder="Plaintext" value={plaintext} onChange={e => setPlaintext(e.target.value)} />
          <input className="form-control mb-3" placeholder="Key" value={key} onChange={e => setKey(e.target.value)} />
          <button className="btn btn-success" onClick={encrypt}>Encrypt</button>
          <p className="mt-3"><strong>Ciphertext:</strong> <code>{ciphertext}</code></p>
        </div>

        <div className="col-md-6">
          <h4 className="text-warning">Decrypt</h4>
          <input className="form-control mb-2" placeholder="Ciphertext (base64)" value={ciphertext} onChange={e => setCiphertext(e.target.value)} />
          <input className="form-control mb-3" placeholder="Key" value={key} onChange={e => setKey(e.target.value)} />
          <button className="btn btn-warning" onClick={decrypt}>Decrypt</button>
          <p className="mt-3"><strong>Decrypted:</strong> <code>{decrypted}</code></p>
        </div>
      </div>
    </div>
  );
}
