import React, { useState } from 'react';
import axios from 'axios';

export default function AESFileEncryptor() {
  const [key, setKey] = useState('');
  const [file, setFile] = useState(null);
  const [error, setError] = useState('');
  const [downloadUrl, setDownloadUrl] = useState(null);
  const [downloadName, setDownloadName] = useState('');
  const [deleteOriginal, setDeleteOriginal] = useState(false); // NEW state

  const handleUpload = (e) => {
    const selectedFile = e.target.files[0];
    setFile(selectedFile);
  };

  const encryptOrDecrypt = async (action) => {
    if (!file || key.length !== 16) {
      setError('Please select a file and ensure the key is 16 characters.');
      return;
    }

    const formData = new FormData();
    formData.append('file', file);
    formData.append('key', key);

    try {
      const response = await axios.post(`http://localhost:5000/${action}-file/aes`, formData, {
        responseType: 'blob',
        headers: { 'Content-Type': 'multipart/form-data' },
      });

      const blob = new Blob([response.data]);
      const url = URL.createObjectURL(blob);
      setDownloadUrl(url);

      const fileNameParts = file.name.split('.');
      const baseName = fileNameParts.slice(0, -1).join('.') || file.name;
      const extension = fileNameParts.length > 1 ? '.' + fileNameParts.pop() : '';
      const label = action === 'encrypt' ? 'Encrypted' : 'Decrypted';
      setDownloadName(`${baseName} - ${label}${extension}`);

      setError('');
    } catch (err) {
      setError('Failed to process file.');
    }
  };

  const handleDownload = () => {
    const a = document.createElement('a');
    a.href = downloadUrl;
    a.download = downloadName;
    a.click();
    URL.revokeObjectURL(downloadUrl);
    setDownloadUrl(null); // hide button after download

    if (deleteOriginal) {
      setFile(null); // clear file input from memory
      document.querySelector('input[type="file"]').value = ''; // reset actual input field
    }
  };

  return (
    <div className="container mt-5 text-white">
      <h3>üîê AES File Encryption (Binary-Safe)</h3>
      {error && <div className="alert alert-danger">{error}</div>}

      <input
        type="file"
        className="form-control mb-3"
        onChange={handleUpload}
      />

      <input
        type="text"
        className="form-control mb-3"
        placeholder="Enter 16-character AES key"
        value={key}
        onChange={(e) => setKey(e.target.value)}
      />

      <div className="form-check mb-3">
        <input
          type="checkbox"
          className="form-check-input"
          id="deleteOriginalAES"
          checked={deleteOriginal}
          onChange={(e) => setDeleteOriginal(e.target.checked)}
        />
        <label className="form-check-label" htmlFor="deleteOriginalAES">
          Delete original file after download
        </label>
      </div>

      <div className="d-flex gap-3 mb-3">
        <button className="btn btn-outline-success" onClick={() => encryptOrDecrypt('encrypt')}>
          Encrypt File
        </button>
        <button className="btn btn-outline-warning" onClick={() => encryptOrDecrypt('decrypt')}>
          Decrypt File
        </button>
      </div>

      {downloadUrl && (
        <button className="btn btn-primary" onClick={handleDownload}>
          Download Result
        </button>
      )}
    </div>
  );
}
