import { useState } from "react";
import axios from "axios";
import { Loader2 } from "lucide-react";

export default function TranspositionPage() {
  const [plaintext, setPlaintext] = useState("");
  const [key, setKey] = useState("");
  const [ciphertext, setCiphertext] = useState("");
  const [decryptedText, setDecryptedText] = useState("");
  const [error, setError] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  const validateInputs = () => {
    if (!plaintext && !ciphertext) {
      setError("Please enter text to encrypt or decrypt");
      return false;
    }
    if (!key || key < 2) {
      setError("Key must be at least 2");
      return false;
    }
    return true;
  };

  const encryptText = async () => {
    if (!validateInputs()) return;

    setIsLoading(true);
    setError("");
    try {
      const response = await axios.post(
        "http://localhost:5000/encrypt/transposition",
        {
          plaintext,
          key: parseInt(key),
        }
      );
      setCiphertext(response.data.ciphertext);
    } catch (err) {
      setError(
        err.response?.data?.error || "Text encryption failed. Please try again."
      );
    } finally {
      setIsLoading(false);
    }
  };

  const decryptText = async () => {
    if (!validateInputs()) return;

    setIsLoading(true);
    setError("");
    try {
      const response = await axios.post(
        "http://localhost:5000/decrypt/transposition",
        {
          ciphertext,
          key: parseInt(key),
        }
      );
      setDecryptedText(response.data.plaintext);
    } catch (err) {
      setError(
        err.response?.data?.error || "Text decryption failed. Please try again."
      );
    } finally {
      setIsLoading(false);
    }
  };

  const generateKey = () => {
    const randomKey = Math.floor(Math.random() * 8) + 2; // Random key between 2 and 9
    setKey(randomKey.toString());
    setError("");
  };

  return (
    <div className="container mt-4">
      <h1 className="text-light text-center">Transposition Cipher</h1>
      <p className="text-secondary text-center">
        A transposition cipher rearranges the letters of the plaintext according
        to a numeric key.
      </p>

      {error && (
        <div
          className="alert alert-danger alert-dismissible fade show"
          role="alert"
        >
          {error}
          <button
            type="button"
            className="btn-close"
            onClick={() => setError("")}
            aria-label="Close"
          ></button>
        </div>
      )}

      <div className="row mb-4">
        <div className="col-md-6 border-end">
          <h4 className="text-success">Encrypt</h4>
          <div className="form-group mb-3">
            <label htmlFor="plaintext" className="form-label text-light">
              Plaintext
            </label>
            <textarea
              id="plaintext"
              className="form-control"
              placeholder="Enter text to encrypt"
              value={plaintext}
              onChange={(e) => setPlaintext(e.target.value)}
              rows="3"
            />
          </div>

          <div className="input-group mb-3">
            <input
              type="number"
              min="2"
              className="form-control"
              placeholder="Key (number ‚â• 2)"
              value={key}
              onChange={(e) => setKey(e.target.value)}
            />
            <button className="btn btn-success" onClick={generateKey}>
              üîê Generate
            </button>
          </div>

          <button
            className="btn btn-success"
            onClick={encryptText}
            disabled={isLoading}
          >
            {isLoading ? (
              <>
                <Loader2 className="me-2" size={20} />
                Encrypting...
              </>
            ) : (
              "Encrypt"
            )}
          </button>
          <div className="mt-3">
            <label className="form-label text-light">Ciphertext:</label>
            <div className="p-2 bg-dark rounded">
              <code className="text-light">{ciphertext}</code>
            </div>
          </div>
        </div>

        <div className="col-md-6">
          <h4 className="text-warning">Decrypt</h4>
          <div className="form-group mb-3">
            <label htmlFor="ciphertext" className="form-label text-light">
              Ciphertext
            </label>
            <textarea
              id="ciphertext"
              className="form-control"
              placeholder="Enter text to decrypt"
              value={ciphertext}
              onChange={(e) => setCiphertext(e.target.value)}
              rows="3"
            />
          </div>

          <div className="input-group mb-3">
            <input
              type="number"
              min="2"
              className="form-control"
              placeholder="Key (number ‚â• 2)"
              value={key}
              onChange={(e) => setKey(e.target.value)}
            />
            <button className="btn btn-success" onClick={generateKey}>
              üîê Generate
            </button>
          </div>

          <button
            className="btn btn-warning"
            onClick={decryptText}
            disabled={isLoading}
          >
            {isLoading ? (
              <>
                <Loader2 className="me-2" size={20} />
                Decrypting...
              </>
            ) : (
              "Decrypt"
            )}
          </button>
          <div className="mt-3">
            <label className="form-label text-light">Decrypted Text:</label>
            <div className="p-2 bg-dark rounded">
              <code className="text-light">{decryptedText}</code>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
