import React, { useState } from 'react';
import axios from 'axios';

function VernamPage() {
  const [file, setFile] = useState(null);
  const [text, setText] = useState('');
  const [key, setKey] = useState('');
  const [result, setResult] = useState('');
  const [mode, setMode] = useState('encrypt');
  const [resultFileName, setResultFileName] = useState('');

  const generateKey = (length) => {
    const characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
    let result = '';
    const charactersLength = characters.length;
    for (let i = 0; i < length; i++) {
      result += characters.charAt(Math.floor(Math.random() * charactersLength));
    }
    return result;
  };

  const handleGenerateKey = () => {
    if (text) {
      setKey(generateKey(text.length));
    } else if (file) {
      setKey(generateKey(256)); // generate a 256 character key for files by default
    } else {
      alert('Enter text or upload a file first to generate a key.');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!key) {
      alert('Key is required!');
      return;
    }
    try {
      if (file) {
        const formData = new FormData();
        formData.append('file', file);
        formData.append('key', key);
        const response = await axios.post(
          `/api/vernam/${mode}-file`,
          formData,
          { responseType: 'blob' }
        );
        const blob = new Blob([response.data]);
        const url = window.URL.createObjectURL(blob);
        const link = document.createElement('a');
        link.href = url;
        link.setAttribute('download', `vernam-${mode}ed-${file.name}`);
        document.body.appendChild(link);
        link.click();
        link.parentNode.removeChild(link);
      } else {
        const response = await axios.post(`/api/vernam/${mode}-text`, { text, key });
        setResult(response.data.result);
      }
    } catch (error) {
      console.error(error);
      alert('An error occurred during the process.');
    }
  };

  return (
    <div className="container">
      <h2>Vernam Cipher</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Text:</label>
          <textarea
            value={text}
            onChange={(e) => setText(e.target.value)}
            disabled={file}
          />
        </div>
        <div>
          <label>OR Upload File:</label>
          <input
            type="file"
            onChange={(e) => {
              setFile(e.target.files[0]);
              setText('');
            }}
          />
        </div>
        <div>
          <label>Key:</label>
          <input
            type="text"
            value={key}
            onChange={(e) => setKey(e.target.value)}
          />
          <button type="button" onClick={handleGenerateKey}>Generate Key</button>
        </div>
        <div>
          <label>Mode:</label>
          <select value={mode} onChange={(e) => setMode(e.target.value)}>
            <option value="encrypt">Encrypt</option>
            <option value="decrypt">Decrypt</option>
          </select>
        </div>
        <button type="submit">Submit</button>
      </form>

      {result && (
        <div>
          <h3>Result:</h3>
          <textarea value={result} readOnly />
        </div>
      )}
    </div>
  );
}

export default VernamPage;
