import { useState } from 'react';
import axios from 'axios';

export default function DESPage() {
  const [plaintext, setPlaintext] = useState('');
  const [key, setKey] = useState('');
  const [ciphertext, setCiphertext] = useState('');
  const [decryptedText, setDecryptedText] = useState('');
  const [error, setError] = useState('');

  const encrypt = async () => {
    if (!plaintext || !key) {
      setError('Please enter both plaintext and a 24-character key.');
      return;
    }
    if (key.length !== 24) {
      setError('Key must be exactly 24 characters long.');
      return;
    }
    try {
      const response = await axios.post('http://localhost:5000/encrypt/des', { plaintext, key });
      setCiphertext(response.data.ciphertext);
      setError('');
    } catch {
      setCiphertext('Encryption failed');
    }
  };

  const decrypt = async () => {
    if (!ciphertext || !key) {
      setError('Please enter both ciphertext and the decryption key.');
      return;
    }
    try {
      const response = await axios.post('http://localhost:5000/decrypt/des', { ciphertext, key });
      setDecryptedText(response.data.plaintext);
      setError('');
    } catch {
      setDecryptedText('Decryption failed');
    }
  };

  return (
    <div className="container mt-4">
     <h1 className="text-light text-center" >DES - Data Encryption Standard</h1>
      <p className="text-secondary mb-4 text-center">
        DES is a symmetric-key algorithm for the encryption of electronic data using a 56-bit key, often extended via Triple DES (3DES).
      </p>
      {error && <div className="alert alert-danger">{error}</div>}
      <div className="row">
        <div className="col-md-6 border-end">
          <h3 className="text-success mb-3">Encrypt</h3>
          <input className="form-control mb-2" placeholder="Enter plaintext" value={plaintext} onChange={e => setPlaintext(e.target.value)} />
          <input className="form-control mb-3" placeholder="Enter 24-char key" value={key} onChange={e => setKey(e.target.value)} />
          <button className="btn btn-success" onClick={encrypt}>Encrypt</button>
          <div className="mt-3"><strong>Ciphertext:</strong> <code>{ciphertext}</code></div>
        </div>
        <div className="col-md-6">
          <h3 className="text-warning mb-3">Decrypt</h3>
          <input className="form-control mb-2" placeholder="Enter ciphertext" value={ciphertext} onChange={e => setCiphertext(e.target.value)} />
          <input className="form-control mb-3" placeholder="Enter 24-char key" value={key} onChange={e => setKey(e.target.value)} />
          <button className="btn btn-warning" onClick={decrypt}>Decrypt</button>
          <div className="mt-3"><strong>Decrypted Text:</strong> <code>{decryptedText}</code></div>
        </div>
      </div>
    </div>
  );
}
